BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "Birds" (
	"Family"	TEXT NOT NULL,
	"Num"	INTEGER NOT NULL,
	"EngName"	TEXT NOT NULL,
	"SciName"	TEXT NOT NULL,
	"MalName"	TEXT,
	"ChName"	TEXT,
	"RarityStatus"	TEXT NOT NULL,
	"LocalStatus"	TEXT NOT NULL,
	"Identification"	TEXT,
	"Habitat"	TEXT,
	"Behaviour"	TEXT,
	"MinSize"	REAL,
	"MaxSize"	REAL,
	"Place"	TEXT,
	"Colour"	TEXT,
	"Action"	TEXT,
	PRIMARY KEY("Num" AUTOINCREMENT)
);
COMMIT;
